// src/components/Signup.js
import React from 'react';
import { Link } from 'react-router-dom';

const Signup = () => {
  return (
    <div className="max-w-md mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Sign Up</h2>
      <form>
        <input type="text" placeholder="UserName" className="w-full mb-3 p-2 border rounded" required />
        <input type="email" placeholder="Email" className="w-full mb-3 p-2 border rounded" required />
        <input type="password" placeholder="Password" className="w-full mb-3 p-2 border rounded" required />
        <input type="Confirm password" placeholder="Confirm Password" className="w-full mb-3 p-2 border rounded" required />
        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded">Sign Up</button>
        <p className="text-sm mt-2">
          Already have an account? <Link to="/" className="text-blue-600 hover:underline">Login</Link>
        </p>
      </form>
    </div>
  );
};

export default Signup;
