// src/components/ReservationForm.js
import React, { useState } from 'react';
import { createReservation } from '../services/ReservationService';

const ReservationForm = ({ onSuccess }) => {
  const [reservation, setReservation] = useState({
    passengerName: '',
    source: '',
    destination: '',
    dateOfJourney: '',
    trainNumber: '',
    seatNumber: ''
  });

  const handleChange = (e) => {
    setReservation({ ...reservation, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createReservation(reservation);
      alert("Reservation Created!");
      setReservation({
        passengerName: '',
        source: '',
        destination: '',
        dateOfJourney: '',
        trainNumber: '',
        seatNumber: ''
      });
      onSuccess(); // refresh list
    } catch (error) {
      alert("Error creating reservation");
    }
  };
 
  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Reservation</h2>
      <input type="text" name="passengerName" placeholder="Passenger Name" value={reservation.passengerName} onChange={handleChange} required />
      <input type="text" name="source" placeholder="Source" value={reservation.source} onChange={handleChange} required />
      <input type="text" name="destination" placeholder="Destination" value={reservation.destination} onChange={handleChange} required />
      <input type="date" name="dateOfJourney" value={reservation.dateOfJourney} onChange={handleChange} required />
      <input type="text" name="trainNumber" placeholder="Train Number" value={reservation.trainNumber} onChange={handleChange} required />
      <input type="text" name="seatNumber" placeholder="Seat Number" value={reservation.seatNumber} onChange={handleChange} required />
      <button type="submit">Submit</button>
    </form>
  );
};

export default ReservationForm;
