import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../assets/logo.jpg';

const Navbar = () => {
  const [role, setRole] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedRole = localStorage.getItem('userRole');
    setRole(storedRole);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('userRole');
    navigate('/login');
    window.location.reload(); // Refresh navbar state
  };

  return (
    <nav className="bg-gray-200 shadow-md">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo */}
          <div className="flex items-center">
            <img src={logo} alt="Train Logo" className="h-10 w-10 mr-3" />
            <span className="text-xl font-bold tracking-wide">Train Reservation</span>
          </div>

          {/* Navigation Links */}
          <div className="flex space-x-6">
            <Link to="/" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
              Home
            </Link>
            {role === 'ADMIN' && (
              <Link to="/admin/profile" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
                Admin Profile
              </Link>
            )}
            {role === 'CUSTOMER' && (
              <Link to="/user/profile" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
                User Profile
              </Link>
            )}
            <Link to="/create" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
              Create Reservation
            </Link>
            <Link to="/all" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
              All Reservations
            </Link>
            {!role ? (
              <Link to="/login" className="hover:bg-gray-300 px-3 py-2 rounded-md font-medium transition">
                Login
              </Link>
            ) : (
              <button onClick={handleLogout} className="bg-red-500 text-white px-3 py-2 rounded-md">
                Logout
              </button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
