// src/components/ReservationList.js
import React, { useEffect, useState } from 'react';
import { getAllReservations, deleteReservation } from '../services/ReservationService';

const ReservationList = () => {
  const [reservations, setReservations] = useState([]);

  const fetchReservations = async () => {
    const response = await getAllReservations();
    setReservations(response.data);
  };

  const handleDelete = async (id) => {
    await deleteReservation(id);
    fetchReservations();
  };

  useEffect(() => {
    fetchReservations();
  }, []);

  return (
    <div>
      <h2>All Reservations</h2>
      <ul>
        {reservations.map(res => (
          <li key={res.id}>
            {res.passengerName} - {res.source} to {res.destination} on {res.dateOfJourney}
            <button onClick={() => handleDelete(res.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ReservationList;
