// src/components/Signup.js
import React from 'react';
import { Link } from 'react-router-dom';

const Signup = () => {
  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-6 bg-white rounded shadow-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
        <form>
          <input type="text" placeholder="Name" className="w-full mb-3 p-2 border rounded" required />
          <input type="email" placeholder="Email" className="w-full mb-3 p-2 border rounded" required />
          <input type="password" placeholder="Password" className="w-full mb-3 p-2 border rounded" required />
          <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white p-2 rounded">Sign Up</button>
          <p className="text-sm mt-2 text-center">
            Already have an account?{" "}
            <Link to="/" className="text-blue-600 hover:underline">Login</Link>
          </p>
        </form>
      </div>
    </div>
  );
};

export default Signup;
